mapper.select:
SELECT * FROM	fr_user
WHERE			fr_id  = #{fr_id}
AND				fr_pwd = #{fr_pwd}


SELECT fr_id
FROM   fr_user
WHERE  fr_name  = #{fr_name}
AND    fr_email = #{fr_email}


SELECT COUNT(*)
FROM   fr_user
WHERE  fr_id  = #{fr_id}

SELECT	fr_pwd
FROM	fr_user
WHERE	fr_id    = #{fr_id}
AND	    fr_email = #{fr_email}

mapper.update:
	UPDATE fr_user
	SET    fr_pwd      = #{fr_pwd},
		   fr_name     = #{fr_name}, 
		   fr_p_number = #{fr_p_number},
		   fr_email    = #{fr_email}
    WHERE  fr_id       = #{fr_id}
